import React from 'react';
import { PublicClientApplication } from '@azure/msal-browser';
import { MsalProvider } from '@azure/msal-react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import HomePage from './pages/HomePage';
import PersonalPage from './pages/PersonalPage';
import SharedPage from './pages/SharedPage';

const msalConfig = {
  auth: {
    clientId: 'YOUR_CLIENT_ID', // Azure AD uygulama kaydınızdan alınan clientId
    authority: 'https://login.microsoftonline.com/YOUR_TENANT_ID', // Azure AD tenant ID
    redirectUri: 'http://localhost:3000' // React uygulamanızın yönlendirme URI'si
  }
};

const pca = new PublicClientApplication(msalConfig);

const App = () => {
  return (
    <MsalProvider instance={pca}>
      <Router>
        <Switch>
          <Route exact path="/" component={LoginPage} />
          <Route path="/home" component={HomePage} />
          {/* Optional: Add routes for personal and shared pages */}
          {/* <Route path="/home/personal" component={PersonalPage} />
              <Route path="/home/shared" component={SharedPage} /> */}
        </Switch>
      </Router>
    </MsalProvider>
  );
};

export default App;
