import React from 'react';
import { PublicClientApplication, InteractionType } from '@azure/msal-browser';
import { MsalProvider } from '@azure/msal-react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import LoginPage from './LoginPage';
import HomePage from './HomePage';

const msalConfig = {
    auth: {
        clientId: 'Y9f284815-0055-42d1-8fec-5aba570e5e2f', // Azure AD uygulama kaydınızdan alınan clientId
        authority: 'https://login.microsoftonline.com/3884b741-6fbc-4df8-9924-a522e881d601', // Azure AD tenant ID
        redirectUri: 'http://localhost:3000' // React uygulamanızın yönlendirme URI'si
    }
};

const pca = new PublicClientApplication(msalConfig);

const App = () => {
    return (
        <MsalProvider instance={pca}>
            <Router>
                <Switch>
                    <Route exact path="/" component={LoginPage} />
                    <Route path="/home" component={HomePage} />
                </Switch>
            </Router>
        </MsalProvider>
    );
};

export default App;
