import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PersonnelPage = () => {
  const [accounts, setAccounts] = useState([]);
  const [newAccount, setNewAccount] = useState({ username: '', password: '', notes: '' });

  useEffect(() => {
    axios.get('/api/accounts/personal').then(response => {
      setAccounts(response.data);
    });
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewAccount({ ...newAccount, [name]: value });
  };

  const handleAddAccount = () => {
    axios.post('/api/accounts', newAccount).then(response => {
      setAccounts([...accounts, response.data]);
      setNewAccount({ username: '', password: '', notes: '' });
    });
  };

  return (
    <div>
      <h2>Personnel Accounts</h2>
      <ul>
        {accounts.map((account) => (
          <li key={account.id}>{account.username} - {account.notes}</li>
        ))}
      </ul>
      <h3>Add New Account</h3>
      <input type="text" name="username" value={newAccount.username} onChange={handleInputChange} placeholder="Username" />
      <input type="password" name="password" value={newAccount.password} onChange={handleInputChange} placeholder="Password" />
      <textarea name="notes" value={newAccount.notes} onChange={handleInputChange} placeholder="Notes"></textarea>
      <button onClick={handleAddAccount}>Add Account</button>
    </div>
  );
};

export default PersonnelPage;
